import Vue from 'vue';
import VueRouter from 'vue-router';
import Home from './views/Home.vue'; // Import your components

Vue.use(VueRouter);

const routes = [
  {
    path: '/',
    name: 'Home',
    component: Home
  },
  {
    path: '/subpage',
    name: 'Subpage',
    component: () => import('./views/Subpage.vue') // Import your subpage component
  }
];

const router = new VueRouter({
  mode: 'history', // Use 'history' mode for clean URLs
  routes
});

export default router;
